import React, { useState, useRef } from "react";
import Card from "../UI/Card";
import Button from "../UI/Button";
import ErrorModal from "../UI/ErrorModal";
import Wrapper from "../Helpers/wrapper";
// use css module :
import classes from "./AddUser.module.css";
const AddUser = (props) => {
  const nameInputRef = useRef();
  const ageInputRef = useRef();
  const [enteredUsername, setEnteredUsername] = useState("");
  const [enteredAge, setEnteredAge] = useState("");
  const [error, setError] = useState();

  const addUserHandler = (event) => {
    event.preventDefault();
    const enteredName = nameInputRef.current.value;
    const enteredUserAge = ageInputRef.current.value;
    // to check validation
    if (enteredName.trim().length === 0 || enteredUserAge.trim().length === 0) {
      setError({
        title: "Invalid Input",
        massage: "Please enter a valid name and age (non-empty values)",
      });
      return;
    }
    //  '+' help force convension from string to number
    if (+enteredUserAge < 1) {
      setError({
        title: "Invalid Age",
        massage: "Please enter a valid age (> 0)",
      });
      return;
    }
    props.onAddUser(enteredName, enteredUserAge);
    // to reset values after submit
    // setEnteredUsername("");
    // setEnteredAge("");
  };
  // we use Ref instead
  // const usernameChangeHandler = (event) => {
  //   //set changes for every keystroke in input
  //   setEnteredUsername(event.target.value); // get the value the user currently entered
  // };
  // const ageChangeHandler = (event) => {
  //   setEnteredAge(event.target.value);
  // };
  const errorHandler = () => {
    setError(null);
  };
  return (
    // we use wrapper component to have one root element which won't render anything to the DOM
    <Wrapper>
      {error && (
        <ErrorModal
          title={error.title}
          massage={error.massage}
          onConfirm={errorHandler}
        ></ErrorModal>
      )}
      {/* // Card component is used to set style around the form input */}
      <Card className={classes.input}>
        <form onSubmit={addUserHandler}>
          {/* for is a keyName in js and it is not an attribute => so we use : htmlFor for label in form */}
          <label htmlFor="username">Username : </label>
          {/* value reflect current state */}
          <input id="username" type="text" Ref={nameInputRef} />
          <label htmlFor="age">Age (Years) : </label>
          <input id="age" type="number" Ref={ageInputRef} />
          <Button type="submit">Add User</Button>
        </form>
      </Card>
    </Wrapper>
  );
};
export default AddUser;
