import React, { useState } from "react";
import Card from "../UI/Card";
import Button from "../UI/Button";
// use css module :
import classes from "./AddUser.module.css";
const AddUser = (props) => {
  const [enteredUsername, setEnteredUsername] = useState("");
  const [enteredAge, setEnteredAge] = useState("");

  const addUserHandler = (event) => {
    event.preventDefault();
    // to check validation
    if (enteredUsername.trim().length === 0 || enteredAge.trim().length === 0) {
      return;
    }
    //  '+' help force convension from string to number
    if (+enteredAge < 1) {
      return;
    }
    props.onAddUser(enteredUsername, enteredAge);
    // to reset values after submit
    setEnteredUsername("");
    setEnteredAge("");
  };

  const usernameChangeHandler = (event) => {
    //set changes for every keystroke in input
    setEnteredUsername(event.target.value); // get the value the user currently entered
  };
  const ageChangeHandler = (event) => {
    setEnteredAge(event.target.value);
  };

  return (
    // Card component is used to set style around the form input
    <Card className={classes.input}>
      <form onSubmit={addUserHandler}>
        {/* for is a keyName in js and it is not an attribute => so we use : htmlFor for label in form */}
        <label htmlFor="username">Username : </label>
        {/* value reflect current state */}
        <input
          id="username"
          type="text"
          value={enteredUsername}
          onChange={usernameChangeHandler}
        />
        <label htmlFor="age">Age (Years) : </label>
        <input
          id="age"
          type="number"
          value={enteredAge}
          onChange={ageChangeHandler}
        />
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};
export default AddUser;
